sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();

	// Query the database for NeedIt Task records with Due date field values within
	// 24 hours from now.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var dueNITasks = new GlideRecord('x_58872_needit_needit_task');
	dueNITasks.addQuery('due_date','>=',rightNow);
	dueNITasks.addQuery('due_date','<',gs.hoursAgo(-24));
	dueNITasks.addQuery('state','!=',3);

	dueNITasks.query();
	// Write a log message for each due NeedIt Task Record
	while(dueNITasks.next()){
		gs.info("NeedIt Task due soon record = " + dueNITasks.number);
		gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',dueNITasks,dueNITasks.number,gs.getUserName());
	}
